  private Comparator c = new Comparator() 
  {
    public int compare(Object obj1, Object obj2) 
    {
      String o1 = ((File) obj1).getName();
      String o2 = ((File) obj2).getName();
      o1 = o1.substring(o1.indexOf('@'));
      o2 = o2.substring(o2.indexOf('@'));
      return -1*o1.compareTo(o2);
    }
  };

  /**
   * 
   * Gets the attribute value for the calculated attribute DirAll
   */
  public String getDirAll()
  {
    StringBuffer buf = new StringBuffer();
    String dirName = Constants.XLS_FILES_PATH + Constants.DIR_PROJEKTY_DOKLADY+"\\"+getId();
    File dir = new File(dirName);
    FileFilter ff = getFileFilter(null);
    File[] arr = dir.listFiles(ff);
    if(arr != null) {
      Arrays.sort(arr,c);
      for( int i=0; i<arr.length; i++ ) 
      {
        String name = arr[i].getName();
        int indexMinus = name.indexOf('@');
        String idDoklad = name.substring(0,indexMinus);
        buf.append("<a href=\"ProjektyBilanceDelete.jsp?file="+Constants.DIR_PROJEKTY_DOKLADY+"|5C"+getId()+"|5C" + name + "&idDoklad="+idDoklad+"\" title=\"Smazat\">DEL</a>\n&nbsp;");
        buf.append("<a target=\"_blank\" href=\"excelservlet/"+idDoklad+"?file="+Constants.DIR_PROJEKTY_DOKLADY+"|5C"+getId()+"|5C" + name + "\">" + name.substring(indexMinus+1)+ "</a>");
        buf.append("&nbsp;<a href=\"LogsProjektDoklad.jsp?idDoklad="+idDoklad+"\">Logs</a>");
        buf.append("&nbsp;" + df.format( new java.util.Date(arr[i].lastModified()))+"<br>"); 
      }
    }
    if(buf.length()<1) return "&nbsp;";
    return buf.toString();
  }

